import { useState } from "react";
import { AsyncPaginate } from "react-select-async-paginate";

const Search=({onSearchChange}) => {

    const [search , setSearch]= useState(null);
    const handleOnChange =(searchData) =>{
        setSearch(searchData);
        onSearchChange(searchData);
    }

    return(
        <AsyncPaginate /*only component we will use here and it has parametres*/
        placeholder="Search for city"/*The placeholder attribute specifies a short hint that describes the expected value of a text field.*/
        debounceTimeout={600}/*controlling the frequency at which time-consuming tasks are triggered.*/
        value={search}
        onChange={handleOnChange}/*React onChange is an event handler that triggers when there is any change in the input field.*/
        />
    )
}

export default Search;
