import { useState } from "react";
import { AsyncPaginate } from "react-select-async-paginate";
import { GEO_API_URL, geoApiOptions } from '../../api';

const Search = ({ onSearchChange }) => {/*onSearchChange is prop*/

    const [search, setSearch] = useState(null);

    const loadOptions = (inputvalue) => {
        return fetch(`${GEO_API_URL}/cities?minPopulation=1000000&namePrefix=${inputvalue}`, geoApiOptions)/*ADDing geo.. so that its easier to implement variables*/
            /*minPopulation=1000000: This query parameter filters the cities to only those with a population of at least 1,000,000.*/
            /*namePrefix=${inputValue}: This query parameter filters cities whose names start with the inputValue provided by the user.*/
            .then(response => response.json())
            .then(response => {
                return {
                    options: response.data.map((city) => {
                        return {
                            value: `${city.latitude} ${city.longitude}`,
                            label: `${city.name}, ${city.countryCode}`,//is used to define a label for an <input> element in a form.
                        }
                    })
                }
            })
            .catch(err => console.error(err));
    }
    const handleOnChange = (searchData) => {
        setSearch(searchData);
        onSearchChange(searchData);
    }

    return (
        <AsyncPaginate /*only component we will use here and it has parametres*/
            placeholder="Search for city"/*The placeholder attribute specifies a short hint that describes the expected value of a text field.*/
            debounceTimeout={600}/*controlling the frequency at which time-consuming tasks are triggered.*/
            value={search}
            onChange={handleOnChange}/*React onChange is an event handler that triggers when there is any change in the input field.*/
            loadOptions={loadOptions}/*The function to call for loading options.*/
        />
    )
}

export default Search;
